mapper.select:
SELECT n.id, n.user_id, n.type, n.related_type, n.related_id, n.sender_id, n.content, n.is_read, n.created_at, u.username as sender_username, u.nickname as sender_nickname, u.avatar as sender_avatar, COALESCE(p.title, a.title, c.content) as related_title, COALESCE(SUBSTRING(p.content, 1, 100), SUBSTRING(a.description, 1, 100), SUBSTRING(c.content, 1, 100)) as related_preview FROM notifications n INNER JOIN users u ON n.sender_id = u.id LEFT JOIN posts p ON n.related_type = 'post' AND n.related_id = p.id LEFT JOIN activities a ON n.related_type = 'activity' AND n.related_id = a.id LEFT JOIN comments c ON n.related_type = 'comment' AND n.related_id = c.id WHERE n.id IN
#{notificationId}
AND n.user_id = #{userId} ORDER BY n.created_at DESC

mapper.update:
UPDATE notifications 
SET is_read = 1
WHERE id = #{notificationId}

UPDATE notifications 
SET is_read = 1
WHERE id IN

    #{notificationId}

AND user_id = #{userId}

mapper.delete:
DELETE FROM notifications 
WHERE id IN

    #{notificationId}

AND user_id = #{userId}

mapper.select:
SELECT COUNT(1) 
FROM notifications 
WHERE user_id = #{userId} AND type = #{type}

SELECT COUNT(1) > 0 
FROM notifications 
WHERE id = #{notificationId} AND user_id = #{userId}
